function itemKey(type, category, simple) {
  if (simple) {
    return `${type}||${category}||SIMPLE`;
  } else {
    return `${type}||${category}`;
  }
}

function initializeMenuSheetItems(table) {
  /*
   * Based on the 'menuSheetItemTable', This function makes 'menuItems' like below.
   * ```
   *   menuItems["ALBUM_SPINE||OTHER_SETTINGS"] = [
   *     {label: 'BOXを作成してアルバムを整頓する',
   *       submenu: [
   *         {label: '写真区分でアルバムを整頓する…', actionId: 'BOX:NEW-BY-PHOTO-CLASSIFICATION', },
   *         {label: '工種でアルバムを整頓する…', actionId: 'BOX:NEW-BY-CONSTRUCTION-TYPE', }
   *       ]
   *     },
   *     {label: 'ブックカバーを編集', actionId: '', },
   *     {label: 'アルバムを複製',  actionId: '', },
   *     {label: 'フレームの回転',
   *       submenu: [
   *         {label: '回転を元に戻す', actionId: '', },
   *       ],       
   *     },
   *     {label: '全ての画像を閉じる', actionId: 'PHOTOVIEW:CLOSE-ALL', },
   *     {label: '確定', actionId: 'PHOTOEDIT:FIX-CLIPART', },
   *   ];
   *   menuItems["BOOKRACK||OTHER_SETTINGS"] = [
   *     :
   *   ],
   *    :
   * ```
   *
   */
  let result = [];

  for (let tblItem of table) {
    if (tblItem.actionId==='') continue;

    for (let simple of [false, true]) {
      if (simple && !tblItem.isSimple) {
        continue;
      }

      for (let type of tblItem.types) {
        assert(type);
        assert(tblItem.categoryId);

        let key = itemKey(type, tblItem.categoryId, simple);
        if (!result.hasOwnProperty(key)) {
          result[key] = [];
        }

        let pushTarget = result[key];
        for (let pl of tblItem.parentLabels) {
          let parentItem = pushTarget.find(i => i.label===pl);
          if (!parentItem) {
            parentItem = {
              label: pl,
              submenu: [],
            };
            pushTarget.push(parentItem);
          }
          pushTarget = parentItem.submenu;
        }
        pushTarget.push({
          label: tblItem.label,
          actionId: tblItem.actionId,
        });
      }
    }
  }
  logger.info('menu sheet item was initialized');

  return result;
}

async function updateItem(item, menuType, target) {
  switch (item.actionId) {
    case 'BOX:NEW-BY-PHOTO-CLASSIFICATION':
      {
        let construction = await target.constructionInformation;
        if (construction.knack.knackType===3) {
          item.label = '施工状況でアルバムを整頓する...';
        } else if (construction.knack.knackType===9) {
          item.label = `${construction.photoInformationTags[1]}でアルバムを整頓する...`;
        } else {
          item.label = '写真区分でアルバムを整頓する...';
        }
      }
      break;
    case 'BOX:NEW-BY-CONSTRUCTION-TYPE':
      {
        let construction = await target.constructionInformation;
        if (construction.knack.knackType===3) {
          item.label = '詳細でアルバムを整頓する...';
        } else if (construction.knack.knackType===9) {
          item.label = `${construction.photoInformationTags[2]}でアルバムを整頓する...`;
        } else {
          item.label = '工種でアルバムを整頓する...';
        }
      }
      break;
    default:
      break;
  }
}

async function updateVisibility(item, menuType, category, target, options) {
  if (item.submenu instanceof Array) {
    for (let child of item.submenu) {
      await updateVisibility(child, menuType, category, target, options);
    }
    item.visible = item.submenu.some(item => item.type!=='separator' && item.visible);
  } else if (typeof item.actionId === 'string') {
    if (item.actionId==='') {
      item.visible = true;
      item.enabled = false;
    } else {
      item.visible = await menuActions.isRunnable(item.actionId, menuType, target, options);
      await updateItem(item, menuType, target);
    }
  } else {
    item.visible = false;
  }
}

module.exports = {
  hasMenuItems(menuType, category, options) {
    return null!=menuItems[itemKey(menuType,category,options.simpleMenu)];
  },

  async getMenuItems(menuType, category, target, options) {
    let items = menuItems[itemKey(menuType,category,options.simpleMenu)];
    if (!items) {
      return [];
    }

    for (let item of items) {
      await updateVisibility(item, menuType, category, target, options);
    }
    return items;
  },
};
